$(document).ready(function(){$("#lookupbtn").click(function(){var domain=$("#domain").val();if(domain==''){alert('Please Enter Domain Name/url')}else{var myHeaders=new Headers();myHeaders.append("apikey","YOUR-API-KEY");var requestOptions={method:'GET',redirect:'follow',headers:myHeaders};fetch("https://api.apilayer.com/whois/query?domain="+domain,requestOptions).then(response=>response.json()).then(function(data){$("#domain_name").html(data.result.domain_name);$("#registrar").html(data.result.registrar);$("#creation").html(data.result.creation_date);$("#expiry").html(data.result.expiration_date);$("#updated").html(data.result.updated_date);$("#email").html(data.result.emails);$("#address").html(data.result.address);$("#city").html(data.result.city);$("#country").html(data.result.country);$("#org").html(data.result.org);$("#state").html(data.result.state);$("#status").html(data.result.status);$("#whois_server").html(data.result.whois_server);$("#zipcode").html(data.result.zipcode);$("#name").html(data.result.name);$("#dnssec").html(data.result.dnssec);var ns=[data.result.name_servers];var nsstring=ns.join();$("#name_servers").html(nsstring);console.log(data.result)})}})})